const byteCode = ""

func main() {
	//deployByAbi()
	deployStoreByByteCode()
}

func deployByAbi() {
	client, err := ethclient.Dial("https://eth-sepolia.g.alchemy.com/v2/xxxxxxxx")

	if err != nil {
		log.Fatal(err)
	}
	defer client.Close()

	//账户私钥
	privateKey, err := crypto.HexToECDSA("xxxxxxxx")
	if err != nil {
		log.Fatal("privateKey err:", err)
	}
	publicKey := privateKey.Public()

	publicKeyECDSA, ok := publicKey.(*ecdsa.PublicKey)
	if !ok {
		log.Fatal("cannot assert type: publicKey is not of type *ecdsa.PublicKey")
	}

	fromAddress := crypto.PubkeyToAddress(*publicKeyECDSA)
	nonce, err := client.PendingNonceAt(context.Background(), fromAddress)
	if err != nil {
		log.Fatal("nonce err:", err)
	}
	gasPrice, err := client.SuggestGasPrice(context.Background())
	if err != nil {
		log.Fatal(err)
	}
	chainId, err := client.NetworkID(context.Background())
	if err != nil {
		log.Fatal(err)
	}

	auth, err := bind.NewKeyedTransactorWithChainID(privateKey, chainId)
	if err != nil {
		log.Fatal(err)
	}

	auth.Nonce = big.NewInt(int64(nonce))
	auth.Value = big.NewInt(0)
	auth.GasLimit = uint64(3000000)
	auth.GasPrice = gasPrice
	input := "1.0"
	address, tx, instance, err := store.DeployStore(auth, client, input)
	if err != nil {
		log.Fatal(err)
	}

	fmt.Println(address.Hex())
	fmt.Println(tx.Hash().Hex())

	_ = instance
}

func deployStoreByByteCode() {
	client, err := ethclient.Dial("https://eth-sepolia.g.alchemy.com/v2/xxxxxxxx")
	if err != nil {
		log.Fatal(err)
	}
	defer client.Close()
	privateKey, err := crypto.HexToECDSA("xxxxxxxx")
	if err != nil {
		log.Fatal("privateKey err:", err)
	}
	publicKey := privateKey.Public()
	publicKeyECDSA, ok := publicKey.(*ecdsa.PublicKey)
	if !ok {
		log.Fatal("cannot assert type: publicKey is not of type *ecdsa.PublicKey")
	}
	fromAddress := crypto.PubkeyToAddress(*publicKeyECDSA)
	nonce, err := client.PendingNonceAt(context.Background(), fromAddress)
	if err != nil {
		log.Fatal("nonce err:", err)
	}
	gasPrice, err := client.SuggestGasPrice(context.Background())
	if err != nil {
		log.Fatal(err)
	}
	decodeString, err := hex.DecodeString(byteCode)
	if err != nil {
		log.Fatal(err)
	}
	tx := types.NewContractCreation(nonce, big.NewInt(0), 300000, big.NewInt(0).Add(gasPrice, big.NewInt(int64(10000000000))), decodeString)
	// 签名交易
	chainID, err := client.NetworkID(context.Background())
	if err != nil {
		log.Fatal(err)
	}
	signedTx, err := types.SignTx(tx, types.NewEIP155Signer(chainID), privateKey)
	if err != nil {
		log.Fatal(err)
	}
	//发送交易
	err = client.SendTransaction(context.Background(), signedTx)

	if err != nil {
		log.Fatal("发送交易失败：", err)
	}
	fmt.Printf("交易发送成功！TxHash：%s\n", signedTx.Hash().Hex())
	receipt, err := waitForReceipt(client, signedTx.Hash())
	if err != nil {
		log.Fatal("获取交易回执失败：", err)
		return
	}
	fmt.Printf("交易成功！ContractAddress：%s\n", receipt.ContractAddress.Hex())

}

func waitForReceipt(client *ethclient.Client, txHash common.Hash) (*types.Receipt, error) {
	for {
		receipt, err := client.TransactionReceipt(context.Background(), txHash)
		if err == nil {
			return receipt, nil
		}
		if err != ethereum.NotFound {
			return nil, err
		}
		// 等待一段时间后再次查询
		time.Sleep(1 * time.Second)
	}
}
